    // Permitir acceso al bot√≥n de enviar
    public void addSendButtonListener(ActionListener listener) {
        sendBtn.addActionListener(listener);
    }

    // Obtener el mensaje escrito por el usuario
    public String getMessageInput() {
        return tflInputMessage.getText().trim();
    }

    // Limpiar el campo de entrada
    public void clearMessageInput() {
        tflInputMessage.setText("");
    }

    // Mostrar mensaje en el panel de mensajes
    public void appendMessage(String message) {
        String currentText = texPMessages.getText();
        texPMessages.setText(currentText + "\n" + message);
    }

    // Permitir cambiar el nombre del cliente
    public void setClientName(String name) {
        nameLbl.setText(name);
    }

    // Permitir cerrar la ventana desde controlador
    public void close() {
        this.dispose();
    }

    // Permitir mostrar la ventana desde el controlador
    public void showView() {
        this.setVisible(true);
    }

    // Permitir acceso a otros componentes si se necesitara
    public JButton getSendButton() {
        return sendBtn;
    }

    public JTextField getInputField() {
        return tflInputMessage;
    }

    public JTextPane getMessagePane() {
        return texPMessages;
    }

